import React, {useState} from 'react';
import './style.css';
import TinderCard from 'react-tinder-card'

function Index() {
    const [people, setPeople] = useState([{
        name: "Elon Musk",
        url: "https://upload.wikimedia.org/wikipedia/commons/thumb/3/34/Elon_Musk_Royal_Society_%28crop2%29.jpg/220px-Elon_Musk_Royal_Society_%28crop2%29.jpg"
    }, {
        name: "Jeff Bezos",
        url: "https://www.biography.com/.image/ar_1:1%2Cc_fill%2Ccs_srgb%2Cg_face%2Cq_auto:good%2Cw_300/MTY2NzA3ODE3OTgwMzcyMjYw/jeff-bezos-andrew-harrer_bloomberg-via-getty-images.jpg"
    }]);

    const swiped = (direction, nameOfDelete) => {
        console.log('Removing: ' + nameOfDelete);
        // setLastDirection(direction);
    }

    const outOfFrame = (name) => {
        console.log(name + ' left the screen');
    }

    return (<div className='tinder__cards'>
        <div className="tinder__cards__container">
            {people.map((person) => (
                <TinderCard
                    className='swipe'
                    key={person.name}
                    preventSwipe={['up', 'down']}
                    onSwipe={(dir) => swiped(dir, person.name)}
                    onCardLeftScreen={() => outOfFrame(person.name)}
                >
                    <div className='card' style={{backgroundImage: `url(${person.url})`}}>
                        <h3>{person.name}</h3>
                    </div>
                </TinderCard>))}
        </div>

    </div>);
}

export default Index;
